import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./CustomerDashboard.css";

function CustomerDashboard() {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    axios
      .get("http://localhost:3001/api/products")
      .then((res) => {
        setProducts(res.data.data);
      })
      .catch((err) => console.log(err));
  }, []);

  // const addCart = (product) => {
  //   setCart((prevCart) => ({
  //     ...prevCart,
  //     [product._id]: { ...product, quantity: 1 },
  //   }));
  // };

  // const removeCart = (productId) => {
  //   setCart((prevCart) => {
  //     const updatedCart = { ...prevCart };
  //     delete updatedCart[productId];
  //     return updatedCart;
  //   });
  // };

  const addCart = () => {
    setProducts([...products, products._id]);
  };

  const removeCart = () => {
    setCart([products.filter((p) => p._id !== products._id)]);
  };

  const goToCart = () => {
    navigate("/cart", { state: { cart, products } });
  };

  return (
    <>
      <nav className="navbar navbar-expand-lg bg-light">
        <div className="container-fluid">
          <a className="navbar-brand" href="#">
            Navbar
          </a>
        </div>
      </nav>
      <div className="cart-container">
        {products.map((product) => (
          <div className="product" key={product._id}>
            <div className="img">
              <img src={product.image} alt={product.name} />
            </div>
            <div className="detail">
              <h3>{product.name}</h3>
              <p>Price: ${product.price}</p>
              {products.includes(products) ? (
                <button
                  onClick={() => removeCart()}
                  style={{ backgroundColor: "red" }}
                >
                  <i className="bi bi-x-square-fill"></i>
                  Remove from cart
                </button>
              ) : (
                <button onClick={() => addCart()}>
                  <i className="bi bi-cart-check-fill"></i> Add to cart
                </button>
              )}
            </div>
          </div>
        ))}
      </div>
      <button onClick={goToCart} className="btn btn-primary">
        Go to Cart
      </button>
    </>
  );
}

export default CustomerDashboard;
