import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "bootstrap/dist/js/bootstrap.bundle.min.js";

function CustomerDashboard({ cart, setCart }) {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:3001/api/products")
      .then((res) => {
        setProducts(res.data.data);
      })
      .catch((err) => console.log(err));
  }, []);

  const addCart = () => {
    setCart([...cart, products]);
  };
  const removeCart = () => {
    setCart([cart.filter((c) => c.name !== products.name)]);
  };

  return (
    <>
      <nav class="navbar navbar-expand-lg bg-body-tertiary">
        <div class="container-fluid">
          <a class="navbar-brand" href="#">
            Navbar
          </a>
          <button
            class="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarTogglerDemo02"
            aria-controls="navbarTogglerDemo02"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span class="navbar-toggler-icon"></span>
          </button>
          <div class="collapse navbar-collapse" id="navbarTogglerDemo02">
            <button class="btn btn-outline-success" type="submit">
              Search
            </button>
          </div>
        </div>
      </nav>
      <div style={{ "background-color": "#1b1a1a ", "padding-top": "250px" }}>
        <h1 className="heading">Product Details</h1>
        <div className="cart-container">
          {products.map((product) => (
            <div className="product" key={product.id}>
              <div className="img">
                <img src={product.image} alt={product.name} />
              </div>
              <div className="detail">
                <h3>{product.name}</h3>
                <p>Price ${product.price}</p>
                <button onClick={addCart}>addCart</button>
                <br />
                <br />
                <button onClick={removeCart}>removeCart</button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </>
  );
}

export default CustomerDashboard;
